export function Gradient5(ctx) {
  // 텍스트 스타일 세팅
  ctx.textAlign = 'center';
  ctx.textBaseline = 'middle';
  ctx.lineWidth = 2;
  ctx.font = '25px Arial';

  // 상태 A 세팅 (선형 그라디언트)
  // createLinearGradient(첫째 colorStop x 중간값, 첫째 colorStop y 중간값, 둘째 colorStop x 중간값, 둘째 colorStop y 중간값)
  const verticalGrad = ctx.createLinearGradient(0, 200, 0, 350);
  verticalGrad.addColorStop(0, 'red');
  verticalGrad.addColorStop(1, 'green');
  ctx.fillStyle = verticalGrad;
  ctx.strokeStyle = 'yellow';

  // 네모 1 드로잉
  ctx.fillRect(25, 200, 100, 150);
  ctx.strokeText('Fig 1', 75, 225);
  ctx.strokeText('State A', 75, 300);

  // 상태 A 저장
  ctx.save();

  // 상태 B 세팅 (방사형 그라디언트)
  // createRadialGradient(첫째 colorStop x값,y값, 원의 시작 점, 둘째 ==)
  const redGrad = ctx.createRadialGradient(375, 100, 5, 375, 100, 20);
  redGrad.addColorStop(0, 'orange');
  redGrad.addColorStop(1, 'yellow');
  ctx.fillStyle = redGrad;
  ctx.strokeStyle = 'green';

  // 네모 2 드로잉
  ctx.fillRect(175, 0, 100, 150);
  ctx.strokeText('Fig 2', 225, 225);
  ctx.strokeText('State B', 225, 300);
}
