import React, { useEffect } from 'react';
import Button from './tools/Button';
import './css/Home.css';

import gsap from 'gsap/all';
import { Link } from 'react-router-dom';

import { Helmet } from 'react-helmet';

function Home() {
  useEffect(() => {
    // title 효과
    gsap.to('#title', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    // link 효과
    gsap.to('#link', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    gsap.to('.disappearBg', {
      opacity: 0,
      delay: 5,
      duration: 2
    });
  });

  return (
    <>
      <div className="container" id="container">
        <div className="disappearBg"></div>
        <h1 className="title" id="title">
          WELCOME MY PORTFOLIO
        </h1>

        <Button />
        <Link to="/main" className="link" id="link">
          Enter
        </Link>
      </div>
      <Helmet>
        <title>My Protfolio</title>
        <script src="https://cdn.rawgit.com/bradley/Blotter/master/build/blotter.min.js"></script>
        <script src="https://cdn.rawgit.com/bradley/Blotter/3007fe6e/build/materials/fliesMaterial.js"></script>
        <script>console.log(document)</script>
      </Helmet>
    </>
  );
}

export default Home;
