import React, { useEffect } from 'react';
import './css/Main.css';
import gsap from 'gsap/gsap-core';

function Main() {
  useEffect(() => {
    // left side
    gsap.to('#profile', {
      opacity: 0.4,
      duration: 1,
      skewX: -15
    });
    document.getElementById('profile').addEventListener('mouseover', () => {
      gsap.to('#profile', {
        opacity: 1
      });
      gsap.to('#profileTitle', {
        backgroundColor: 'rgba(0,0,0,0.4)',
        border: 0,
        duration: 0.1
      });
    });
    document.getElementById('profile').addEventListener('mouseout', () => {
      gsap.to('#profile', {
        opacity: 0.4
      });
      gsap.to('#profileTitle', {
        backgroundColor: 'rgba(0,0,0,0)',
        border: 1
      });
    });
    document.getElementById('profile').addEventListener('click', () => {
      gsap.to('#profile', {
        onComplete: () => {
          window.location = '/main/#';
        }
      });
    });

    // right side
    gsap.to('#myWork', {
      opacity: 0.4,
      duration: 1,
      skewX: -15
    });
    document.getElementById('myWork').addEventListener('mouseover', () => {
      gsap.to('#myWork', {
        opacity: 1
      });
    });
    document.getElementById('myWork').addEventListener('mouseout', () => {
      gsap.to('#myWork', {
        opacity: 0.4
      });
    });
    document.getElementById('myWork').addEventListener('click', () => {
      gsap.to('#myWork', {
        onComplete: () => {
          window.location = '/main/##';
        }
      });
    });
  });

  return (
    <div id="mainPage">
      <div id="profileTitle">About me</div>
      <div id="profile">
        <div className="background-image1"></div>
      </div>
      <div id="myWorkTitle">My Work</div>
      <div id="myWork">
        <div className="background-image2"></div>
      </div>
    </div>
  );
}

export default Main;
