export function Circle(x, y, dx, dy, radius, ctx, circleArray) {
  const colorArray = ['#9de0ff', '#57ACE2', '#0072B5', '#254969'];

  const minRadius = radius;
  const maxRadius = radius * 12;
  const color = colorArray[parseInt(Math.random() * colorArray.length)];

  const draw = () => {
    ctx.beginPath();
    ctx.arc(x, y, radius, 0, Math.PI * 2, false);
    ctx.fillStyle = color;
    ctx.fill();
  };

  const update = () => {
    if (x + radius > window.innerWidth || x - radius < 0) {
      dx = -dx;
    }
    if (y + radius > window.innerWidth || y - radius < 0) {
      dy = -dy;
    }

    x += dx;
    y += dy;
    draw();
  };

  const animate = () => {
    requestAnimationFrame(animate);
    ctx.clearRect(0, 0, window.innerWidth, window.innerHeight);

    for (let i = 0; i < circleArray.length; i++) {
      circleArray[i].update();
    }
  };
}
