    ctx.arc(x, y, radius, 0, Math.PI * 2, false);
    ctx.fillStyle = color;
    ctx.fill();
  };
  const circleArray = [];

  for (let i = 0; i < 800; i++) {
    const radius = Math.random() * 8 + 1;
    const x = Math.random() * (window.innerWidth - radius * 2) + radius;
