import gsap, { ScrollTrigger, ScrollToPlugin, TextPlugin } from 'gsap/all';
import React, { useEffect } from 'react';

import './css/AboutMe.css';
import HomeButton from './tools/HomeButton';

import Motivation from '../static/images/motivation.jpg';

function AboutMe() {
  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger, ScrollToPlugin, TextPlugin);

    // // anchor 옵션
    const panelsSection = document.querySelector('.overX');
    const panelsContainer = document.querySelector('.row');

    // document.querySelectorAll('.anchor').forEach((anchor) => {
    //   anchor.addEventListener('click', function (e) {
    //     e.preventDefault();

    //     const targetElem = document.querySelector(
    //       e.target.getAttribute('href')
    //     );
    //     if (
    //       targetElem &&
    //       panelsContainer.isSameNode(targetElem.parentElement)
    //     ) {
    //       const containerOffset =
    //         panelsSection.offsetTop + targetElem.offsetLeft;

    //       gsap.to(window, {
    //         scrollTo: {
    //           y: containerOffset * 1.5,
    //           autoKill: false
    //         },
    //         duration: 1
    //       });
    //     } else {
    //       gsap.to(window, {
    //         scrollTo: {
    //           y: targetElem,
    //           autoKill: false
    //         },
    //         duration: 1
    //       });
    //     }
    //   });
    // });

    // 패널 옵션
    const sections = gsap.utils.toArray('.panel');
    let maxWidth = 0;

    const getMaxWidth = () => {
      maxWidth = 0;
      sections.forEach((section) => {
        maxWidth += section.offsetWidth;
      });
    };
    getMaxWidth();

    gsap.to(sections, {
      xPercent: -100 * (sections.length - 1),
      ease: 'none',
      scrollTrigger: {
        trigger: '.overX',
        pin: true,
        scrub: 1,
        snap: 1 / (sections.length - 1),
        end: () => `+=${maxWidth}`,
        invalidateOnRefresh: true
      }
    });

    ScrollTrigger.addEventListener('refreshInit', getMaxWidth);

    // 토글 효과 추가
    const titleList = [
      "I'm a<br /> Beginner<br/> Frontend<br /> Developer",
      'Introduce <br/> My self',
      'Motivation'
    ];

    document.querySelectorAll('.anchor').forEach((anc) => {
      anc.addEventListener('click', (e) => {
        e.preventDefault();

        console.log(targetElem.offsetLeft);
        const targetElem = document.querySelector(
          e.target.getAttribute('href')
        );
        if (
          targetElem &&
          panelsContainer.isSameNode(targetElem.parentElement)
        ) {
          gsap.to(window, {
            scrollTo: {
              y: targetElem.offsetLeft
            }
          });
        }
      });

      anc.addEventListener('mouseover', () => {
        gsap.to(anc, {
          opacity: 1
        });
      });
      anc.addEventListener('mouseout', () => {
        gsap.to(anc, {
          opacity: 0.7
        });
      });
    });

    ScrollTrigger.create({
      trigger: document.querySelector('.nx'),
      onToggle: (e) => {
        if (e.progress === 0) {
          gsap.to('.stateBlack2', {
            display: 'block',
            color: 'rgb(0,0,0)',
            opacity: 0.7
          });
          gsap.to('.anchor', {
            display: 'block',
            opacity: 0.7
          });
          gsap.to('.anc2', {
            opacity: 1
          });
        } else if (e.progress > 0) {
          gsap.to('.stateWhite1', {
            color: 'rgb(255,255,255)'
          });
          gsap.to('.anchor', {
            display: 'none',
            opacity: 0
          });
        }
      }
    });

    sections.forEach((sct, i) => {
      ScrollTrigger.create({
        trigger: sct,
        start: () =>
          `top top-=${
            (sct.offsetLeft - window.innerWidth / 2) *
            (maxWidth / (maxWidth - window.innerWidth))
          }`,
        end: () =>
          `+=${sct.offsetWidth * (maxWidth / (maxWidth - window.innerWidth))}`,
        toggleClass: { targets: sct, className: 'move' + i },
        onEnter: () => {
          gsap.to('.p', {
            duration: 1.4,
            text: titleList[i]
          });
          gsap.to(`.anc${i}`, {
            opacity: 1
          });
          gsap.to(`.stateBlack${i}`, {
            color: 'rgb(0,0,0)'
          });
          gsap.to(`.lightOut${i}`, {
            display: 'flex',
            duration: 1.4,
            opacity: 1
          });
        },
        onEnterBack: () => {
          gsap.to('.p', {
            duration: 1.4,
            text: titleList[i]
          });
          gsap.to(`.anc${i}`, {
            opacity: 1
          });
          gsap.to(`.stateWhite${i}`, {
            color: 'rgb(255,255,255)'
          });
          gsap.to(`.lightOut${i}`, {
            display: 'flex',
            duration: 1.4,
            opacity: 1
          });
        },
        onLeave: () => {
          gsap.to(`.anc${i}`, {
            opacity: 0.7
          });
          gsap.to(`.lightOut${i}`, {
