export function Gradient5(ctx) {
  // 텍스트 스타일 세팅
  ctx.textAlign = "center";
  ctx.textBaseline = "middle";
  ctx.lineWidth = 2;
  ctx.font = "25px Arial";

  // 상태 A 세팅
  const verticalGrad = ctx.createLinearGradient(0, 0, 0, 500);
  verticalGrad.addColorStop(0, "red");
  verticalGrad.addColorStop(1, "green");
  ctx.fillStyle = verticalGrad;
  ctx.strokeStyle = "yellow";

  // 네모 1 드로잉
  ctx.fillRect(25, 200, 100, 150);
  ctx.strokeText("Fig 1", 75, 225);
  ctx.strokeText("State A", 75, 300);

  // 상태 A 저장
  ctx.save();

  // 상태 B 세팅
  const redGrad = ctx.createRadialGradient(375, 100, 5, 375, 100, 200);
  redGrad.addColorStop(0, "orange");
  redGrad.addColorStop(1, "yellow");
  ctx.fillStyle = redGrad;
  ctx.strokeStyle = "green";

  // 네모 2 드로잉
  ctx.fillRect(175, 200, 100, 150);
  ctx.strokeText("Fig 2", 225, 225);
  ctx.strokeText("State B", 225, 300);
}
