export function Circle(ctx, radius) {
  const colorArray = ['#9de0ff', '#57ACE2', '#0072B5', '#254969'];

  const minRadius = radius;
  const maxRadius = radius * 10;
  const color = colorArray[parseInt(Math.random() * colorArray.length)];

  const draw = () => {
    ctx.beginPath();
    ctx.arc(x, y, radius, 0, Math.PI * 2, false);
    ctx.fillStyle = color;
    ctx.fill();
  };
}
const circleArray = [];

const init = () => {
  for (let i = 0; i < 100; i++) {
    const radius = Math.random() * 8 + 1;
  }
};
