import GLC from "../GLCommander";

const render = () => {
  GLC.clear(1.0, 0.0, 0.0, 1.0);
};

// eslint-disable-next-line import/no-anonymous-default-export
export default (id) => {
  const canvas = document.querySelector(`#${id}`);

  if (!canvas) {
    return;
  }

  const gl = canvas.getContext("webgl");

  if (!gl) {
    return;
  }

  GLC.init(gl);
};
