import gsap, { ScrollTrigger, ScrollToPlugin } from 'gsap/all';
import React, { useEffect } from 'react';
import styled from 'styled-components';
import './css/AboutMe.css';

const PanelsContainter = styled.div`
  display: flex;
  width: 300%;
  background-color: purple;
`;

const Row = styled.div`
  flex-direction: row;
`;

const Column = styled.div`
  flex-direction: column;
`;

function AboutMe() {
  useEffect(() => {
    ScrollTrigger.defaults({
      toggleActions: 'restart pause resume pause'
    });

    // Main nav
  });

  return (
    <div className="mainContainer">
      {/* nav */}
      <article id="masthead" className="site-header" role="banner">
        <nav>
          <a href="#panel-1" className="anchor">
            Panel 1
          </a>
          <a href="#panel-2" className="anchor">
            Panel 2
          </a>
          <a href="#panel-3" className="anchor">
            Panel 3
          </a>
          <a href="#map" className="anchor">
            Map
          </a>
        </nav>
      </article>

      <article id="content" className="site-content">
        <section id="panel">
          <PanelsContainter id="panels-container">
            <Column>
              <Row>
                <div id="panel-1"></div>
                <div id="panel-2"></div>
                <div id="panel-3"></div>
              </Row>
            </Column>
          </PanelsContainter>

          <section id="map" className="full-screen"></section>
        </section>
      </article>
    </div>
  );
}

export default AboutMe;
