import React, { useEffect } from 'react';
import './css/AboutMe.css';

function AboutMe() {
  useEffect(() => {
    const fullWidth = document.querySelectorAll('.fullWidth');
    const fullHeight = document.querySelectorAll('.fullHeight');

    fullWidth[0].style.width = window.innerWidth + 'px';
    fullHeight[0].style.height = window.innerHeight + 'px';
  });

  return (
    <div className="mainContainer">
      <div className="fullHeight fullWidth pg" id="page1">
        page1
      </div>
      <div className="fullHeight fullWidth pg" id="page2">
        page2
      </div>
      <div className="fullHeight fullWidth pg" id="page3">
        page3
      </div>
    </div>
  );
}

export default AboutMe;
