export function Transform(ctx) {
  ctx.fillStyle = 'red';
  ctx.storkeStyle = 'black';
  ctx.font = '40px Arial';

  ctx.fillRect(0, 5, 150, 40);
  ctx.strokeText('1. Hello', 5, 40);

  // scales horizontally
  // skews horizontally
  ctx.transform(1, 1, 0.8, 1, 40, 40);
  ctx.fillRect(0, 5, 150, 40);
  ctx.strokeText('2. Hello', 5, 40);
}
