export function Circle(x, y, dx, dy, radius, ctx) {
  const colorArray = ['#9de0ff', '#57ACE2', '#0072B5', '#254969'];

  this.x = x;
  this.y = y;
  this.dx = dx;
  this.dy = dy;
  this.radius = radius;
  this.minRadius = radius;
  this.maxRadius = radius * 10;
  this.color = colorArray[parseInt(Math.random() * colorArray.length)];

  this.draw = function () {
    ctx.beginPath();
    ctx.arc(this.x, this.y, this.radius, 0, Math * 2, false);
    ctx.fillStyle = this.color;
    ctx.fill();
  };

  this.update = function () {
    if (this.x + this.radius > window.innerWidth || this.x - this.radius < 0) {
      this.dx = -this.dx;
    }
    if (this.y + this.radius > window.innerWidth || this.y - this.radius < 0) {
      this.dy = -this.dy;
    }
  };
}
