import React, { useEffect } from 'react';
import Button from './tools/Button';
import './css/Home.css';

import gsap, { TextPlugin } from 'gsap/all';
import { Link } from 'react-router-dom';

function Home() {
  useEffect(() => {
    for (let i = 65; i < 91; i++) {
      console.log(String.fromCharCode(i));
    }

    gsap.registerPlugin(TextPlugin);
    // title 효과
    gsap.to('#title', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    // link 효과
    gsap.to('#link', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });

    const Titles = ['WELCOME', 'MY', 'PORTFOLIO'];

    let cursorEffect = gsap.to('.cursorEffect', {
      opacity: 1,
      ease: 'power2.inout',
      repeat: -1
    });
    let masterT1 = gsap.timeline({ repeat: -1 }).pause();
    let boxT1 = gsap.timeline();

    boxT1
      .to('.box', {
        duration: 1,
        width: '17vw',
        delay: 0.5,
        ease: 'power4.inOut'
      })
      .from('.h1', {
        duration: 1,
        y: '7vw',
        ease: 'power3.out'
      })
      .to('.box', {
        duration: 1,
        height: '7vw',
        ease: 'elastic.out',
        onComplete: () => {
          masterT1.play();
        }
      })
      .to('.box', {
        duration: 1,
        autoAlpha: 0.7,
        yoyo: true,
        repeat: -1,
        ease:
          'rough({ template: none.out, strength: 1, points: 20, taper: "none", randomize: true, clamp: false})'
      });
    Titles.forEach((title) => {
      let tl = gsap.timeline();
      tl.to('.text', { duration: 1, text: title });
      masterT1.add(tl);
    });
  });

  return (
    <>
      <div className="container" id="container">
        <h1 className="title" id="title">
          <span className="box"></span>
          <span className="h1"></span>
          <span className="text"></span>
          <span className="cursorEffect">_</span>
        </h1>
        <Button />
        <Link to="/main" className="link" id="link">
          Enter
        </Link>
      </div>
    </>
  );
}

export default Home;
