import gsap, { ScrollTrigger } from 'gsap/all';
import React, { useEffect } from 'react';

import './css/AboutMe.css';
import HomeButton from './tools/HomeButton';

import styled from 'styled-components';

const Circle = styled.div`
  width: 5px;
  height: 5px;
  background-color: 'rgba(255,255,255,0.5)';
  border-radius: 50%;
`;

function AboutMe() {
  useEffect(() => {
    gsap.registerPlugin(ScrollTrigger);
    // gsap.to('.b', {
    //   scrollTrigger: {
    //     trigger: '.b',
    //     start: 'top top',
    //     scrub: 1,
    //     pin: true,
    //     markers: true,
    //     toggleActions: 'restart pause reverse pause'
    //   }
    // });

    // gsap.utils.toArray('.panel').forEach((panel, i) => {
    //   ScrollTrigger.create({
    //     trigger: panel,
    //     start: 'top top',
    //     pin: true,
    //     pinSpacing: false
    //   });
    // });

    const sections = gsap.utils.toArray('.panel');

    gsap.to(sections, {
      xPercent: -100 * (sections.length - 1),
      ease: 'none',
      scrollTrigger: {
        trigger: '.overX',
        pin: true,
        scrub: 1,
        snap: 1 / (sections.length - 1),
        end: () => '+=' + document.querySelector('.overX').offsetWidth
      }
    });
  });
  return (
    <div className="mainContainer">
      <HomeButton />
      <div id="navbar">
        <a href="#panel-1" className="anchor">
          panel 2
        </a>
        <a href="#panel-2" className="anchor">
          panel 1
        </a>
        <a href="#panel-3" className="anchor">
          panel 3
        </a>
      </div>
      <div className="overX ">
        <div className="row">
          <div className="box a panel" id="panel-1">
            panel 1
          </div>
          <div className="box b panel" id="panel-2">
            panel 2
          </div>
          <div className="box c panel " id="panel-3">
            panel 3
          </div>
        </div>
      </div>
      <div className="box nx ">next page</div>
    </div>
  );
}

export default AboutMe;
