import React, { useEffect } from 'react';
import './css/Main.css';
import Puppy from '../static/images/profile.jpg';
import styled from 'styled-components';
import gsap from 'gsap/gsap-core';

const Img = styled.img`
  position: absolute;
  left: 0;
  height: 100%;
`;

function Main() {
  useEffect(() => {
    gsap.to('#profile', {
      opacity: 0.5,
      duration: 1
    });
    document.getElementById('profile').addEventListener('mouseover', () => {
      gsap.to('#profile', {
        opacity: 1
      });
    });
    document.getElementById('profile').addEventListener('mouseout', () => {
      gsap.to('#profile', {
        opacity: 0.5
      });
    });
  });

  return (
    <div id="mainPage">
      <div id="profile">
        <Img src={Puppy} alt="me"></Img>
      </div>
      <h1 id="mainTitle">hello</h1>
      <div id="square"></div>
    </div>
  );
}

export default Main;
