import React, { useEffect } from 'react';
import './css/Home.css';

import gsap from 'gsap';

function Home() {
  useEffect(() => {
    // title 효과
    gsap.to('#title', {
      opacity: 1,
      duration: 3,
      delay: 0.2
    });
    // link 효과
    gsap.to('#link', {
      opacity: 1,
      duration: 3,
      delay: 0.4
    });

    const divWindow = document.getElementById('window');
    divWindow.width = window.innerWidth;
    divWindow.height = window.innerHeight;
    //   canvas.width = window.innerWidth;
    //   canvas.height = window.innerHeight;
    document.getElementById('window').style.cursor = 'none';
    const cursor = document.createElement('div');
    const follow = document.createElement('div');

    cursor.classList.add('cursor');
    document.getElementById('window').appendChild(cursor);

    follow.classList.add('follow');
    document.getElementById('window').appendChild(follow);

    function move(obj, event) {
      obj.style = '';
      obj.style.top = event.clientY + 'px';
      obj.style.left = event.clientX + 'px';

      if (cursor) {
        window.addEventListener('mousemove', function (event) {
          const e = event;
          const t = e.target;
          const f = follow;
          const c = cursor;

          if (t.tagName == 'A') {
            c.style.backgroundColor = 'transparent';

            f.style.top = t.offsetTop + 'px';
            f.style.left = t.offsetLeft + 'px';
            f.style.width = t.clientWidth + 'px';
            f.style.height = t.clientHeight + 'px';
          } else {
            move(c, e);
            move(f, e);
            f.classList.remove('on-focus');
          }
        });
      }
    }
  });

  return (
    <div id="window">
      <div className="container">
        <h1 className="title" id="title">
          WELCOME MY PORTFOLIO
        </h1>
        <div className="linkBox">
          <a href="#" className="link" id="link">
            ENTER
          </a>
        </div>
      </div>
    </div>
  );
}

export default Home;
