export function Scale(ctx) {
  // 원 1
  ctx.strokeStyle = 'green';
  ctx.lineWidth = 5.0;
  ctx.beginPath();
  ctx.arc(100, 500, 80, 0, Math.PI * 2);
  ctx.stroke();

  // 원 2 (scale 적용)
  ctx.scale(0.8, 1.5);
  ctx.strokeStyle = 'green';
  ctx.lineWidth = 5.0;
  ctx.beginPath();
  ctx.arc(300, 500, 80, 0, Math.PI * 2);
  ctx.stroke();
}
