import React, { useEffect } from 'react';
import Button from './tools/Button';
import './css/Home.css';

import gsap, { TimelineLite, TweenLite } from 'gsap/all';
import { Link } from 'react-router-dom';

function Home() {
  useEffect(() => {
    // title 효과
    gsap.to('#title', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    // link 효과
    gsap.to('#link', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    gsap.to('.disappearBg', {
      opacity: 0,
      delay: 5,
      duration: 2
    });
    // const mySplitText = new SplitText('#title', { type: 'words' });
    const mySplitText = document.getElementById('title').textContent.split(' ');
    const tl = new TimelineLite();
    const numWords = mySplitText.length;

    const mySplit = mySplitText.map((st) => '<div>' + st + '</div>');
    console.log(mySplit[0]);

    TweenLite.delayedCall(0.08, function () {
      for (let i = 0; i < numWords; i++) {
        tl.from(
          mySplitText[i],
          1,
          { force3D: true, scale: Math.random > 0.5 ? 0 : 2, opacity: 0 },
          Math.random()
        );
      }
    });

    // const tl = new TimelineLite();
    // const numWords = mySplitText.words.length;

    // TweenLite.delayedCall(0.08, function () {
    //   for (let i = 0; i < numWords; i++) {
    //     tl.from(
    //       mySplitText.words[i],
    //       1,
    //       { force3D: true, scale: Math.random() > 0.5 ? 0 : 2, opacity: 0 },
    //       Math.random()
    //     );
    //   }
    // });
  });

  return (
    <>
      <div className="container" id="container">
        <div className="disappearBg"></div>
        <h1 className="title" id="title">
          WELCOME MY PORTFOLIO
        </h1>

        <Button />
        <Link to="/main" className="link" id="link">
          Enter
        </Link>
      </div>
    </>
  );
}

export default Home;
