import React, { useEffect } from 'react';
import Button from './tools/Button';
import './css/Home.css';

import gsap from 'gsap/all';
import { Link } from 'react-router-dom';

import useScript from './hooks/useScript';

function Home() {
  useScript(
    'https://cdn.rawgit.com/bradley/Blotter/master/build/blotter.min.js'
  );
  const Blotter = useScript(
    'https://cdn.rawgit.com/bradley/Blotter/3007fe6e/build/materials/fliesMaterial.js'
  );

  useEffect(() => {
    // title 효과
    gsap.to('#title', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    // link 효과
    gsap.to('#link', {
      opacity: 1,
      duration: 1,
      delay: 0.2
    });
    gsap.to('.disappearBg', {
      opacity: 0,
      delay: 5,
      duration: 2
    });

    const container = document.getElementById('main');

    const text = new Blotter.Text('CODEGRID', {
      family: 'serif',
      size: 150,
      fill: '#fff',
      paddingLeft: 80,
      paddingRight: 80,
      paddingTop: 80,
      paddingBottom: 80
    });

    const material = new Blotter.FliesMaterial();
    material.uniforms.uPointCellWidth.value = 0.01;
    material.uniforms.uPointRadius.value = 0.3;
    material.uniforms.uSpeed.value = 3;

    const blotter = new Blotter(material, {
      texts: text
    });

    const scope = blotter.forText(text);
    scope.appendTo(container);
  });

  return (
    <>
      <div className="container" id="container">
        <div className="disappearBg"></div>
        <h1 className="title" id="title">
          WELCOME MY PORTFOLIO
        </h1>

        <Button />
        <Link to="/main" className="link" id="link">
          Enter
        </Link>
      </div>
    </>
  );
}

export default Home;
