import React, { useEffect } from 'react';
import './css/Home.css';

import gsap from 'gsap';

function Home() {
  useEffect(() => {
    // title 효과
    gsap.to('#title', {
      opacity: 1,
      duration: 3,
      delay: 0.2
    });
    // link 효과
    gsap.to('#link', {
      opacity: 1,
      duration: 3,
      delay: 0.4
    });
  });
  const cursor = document.querySelector('article');

  const follow = document.querySelector('section');

  function move(obj, event) {
    obj.style = '';
    obj.style.top = event.clientY;
    obj.style.left = event.clientX;
  }

  if (cursor) {
    window.addEventListener('mousemove', function (e) {
      if (e.target.tagName == 'A') {
        cursor.style.backgroundColor = 'transparent';

        follow.style.top = e.target.offsetTop + 'px';
        follow.style.left = e.target.offsetLeft + 'px';
        follow.style.width = e.target.clientWidth + 'px';
        follow.style.height = e.target.clientHeight + 'px';

        follow.classList.add('on-focus');
      } else {
        move(cursor, e);
        move(follow, e);
        follow.classList.remove('on-focus');
      }
    });
  }

  //   window.addEventListener('mousemove', function (e) {
  //     console.log(e.clientX);
  //   });

  return (
    <div className="full">
      <article className="cursor"></article>
      <section className="follow"></section>
      <div className="container">
        <h1 className="title" id="title">
          WELCOME MY PORTFOLIO
        </h1>
        <div className="linkBox">
          <a href="#" className="link" id="link">
            ENTER
          </a>
        </div>
      </div>
    </div>
  );
}

export default Home;
