import React, { createElement, useEffect } from 'react';
import './css/Button.css';

function Button() {
  useEffect(() => {
    const cursor = document.getElementById('cursor');
    cursor.classList.add('cursor');

    const follow = document.getElementById('follow');
    follow.classList.add('follow');

    function move(obj, event) {
      obj.style = '';
      obj.style.top = event.offsetTop + 'px';
      obj.style.left = event.offsetLeft + 'px';
    }

    if (cursor) {
      window.addEventListener('mousemove', function (event) {
        const e = event;
        const t = e.target;
        const f = follow;
        const c = cursor;

        if (t.tagName == 'A') {
          c.style.background = 'transparent';

          f.style.top = t.offsetTop + 'px';
          f.style.left = t.offsetLeft + 'px';
          f.style.width = t.clientWidth + 'px';
          f.style.height = t.clientHeight + 'px';

          f.classList.add('on-focus');
        } else {
          move(c, e);
          move(f, e);
          f.classList.remove('on-focus');
        }
      });
    }
  });
  return (
    <div id="main">
      <div id="cursor"></div>
      <div id="follow"></div>
      <a href="#" className="button">
        Button
      </a>
    </div>
  );
}

export default Button;
