import gsap, { ScrollTrigger, ScrollToPlugin } from 'gsap/all';
import React, { useEffect } from 'react';
import styled from 'styled-components';
import './css/AboutMe.css';

const PanelsContainter = styled.div`
  display: flex;
  width: 300%;
  background-color: purple;
`;

const Row = styled.div`
  width: 100%;
  display: flex;
  flex-direction: row;
  overflow: hidden;
`;

const Column = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
`;

function AboutMe() {
  useEffect(() => {
    ScrollTrigger.defaults({
      toggleActions: 'restart pause resume pause'
    });

    // Main nav
    const panelsSection = document.querySelector('#panels');
    const panelsContainer = document.querySelector('#panels-container');
    document.querySelectorAll('.anchor').forEach((anchor) => {
      anchor.addEventListener('click', function (e) {
        e.preventDefault();

        const targetElem = document.querySelector(
          e.target.getAttribute('href')
        );
        if (targetElem && panelsContainer.isSameNode(targetElem)) {
          const containerOffset =
            panelsSection.offsetTop + targetElem.offsetLeft;

          gsap.to(window, {
            scrollTo: {
              y: containerOffset,
              autoKill: false
            },
            duration: 1
          });
        } else {
          gsap.to(window, {
            scrollTo: {
              y: targetElem,
              autoKill: false
            },
            duration: 1
          });
        }
      });
    });

    // Panels
    const panels = gsap.utils.toArray('#panels-container .panel');
    console.log(panels);
    gsap.to(panels);
  });

  return (
    <div className="mainContainer">
      {/* nav */}
      <article id="masthead" className="site-header">
        <nav>
          <a href="#panel-1" className="anchor">
            Panel 1
          </a>
          <a href="#panel-2" className="anchor">
            Panel 2
          </a>
          <a href="#panel-3" className="anchor">
            Panel 3
          </a>
          <a href="#map" className="anchor">
            Map
          </a>
        </nav>
      </article>

      <article id="content">
        <PanelsContainter id="panels">
          <Column>
            <Row id="panels-container">
              <div id="panel-1" className="full-screen panel"></div>
              <div id="panel-2" className="full-screen panel"></div>
              <div id="panel-3" className="full-screen panel"></div>
            </Row>
          </Column>
        </PanelsContainter>

        <section id="map" className="full-screen"></section>
      </article>
    </div>
  );
}

export default AboutMe;
